function popThemeFromLocalStorage() {
  return localStorage.getItem('pageTheme');
}

let currentPageTheme = popThemeFromLocalStorage();

if (currentPageTheme) {
  document.documentElement.className = currentPageTheme;
} else {
  currentPageTheme = 'light-theme';
}

